Module.register("uvx_magic_mirror", {

    defaults: {
        text: "Hello World!"
    },

    getScripts: function() {
        return ["uvx_magic_mirror.js"];
    },

    start: async function() {

        Log.info('Starting module: ${this.name}');

    },

    getDom: function() {

        var wrapper = document.createElement("div");

        wrapper.innerHTML = this.config.text;
        return wrapper;

    }

} );